INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (17,'Visita del Archivo General de la Nación representado por la Sra. Carmen Aliaga y la Sra. Marlitt Rodríguez', 'Tuvimos una importante visita del Archivo General de la Nación representado por la Sra. Carmen Aliaga y la Sra. Marlitt Rodríguez, para garantizar el cumplimiento de las normas y procedimientos archivísticos. Además, se llevó a cabo una charla de Orientación de la Norma Archivística. En el ICL, estamos comprometidos con la calidad de nuestros servicios, y esta visita de supervisión nos permite garantizar la transparencia, accesibilidad y protección de la información contenida en nuestros archivos.', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/08/1-1-1024x683.jpg', 1, 'Migración', 1);

INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (18,'Reunión de coordinación con el alcalde Alfredo Reynaga de la Municipalidad Independencia', 'El Presidente Ejecutivo del ICL, Luis Molina Arles sostuvo una importante reunión de coordinación con el alcalde Alfredo Reynaga de la Municipalidad de Independencia, para la suscripción del Convenio Marco de Cooperación Técnica Interinstitucional. El Instituto Catastral de Lima está comprometido en seguir trabajando para impulsar el desarrollo y modernización del catastro.', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/08/1-2-1024x683.jpg', 1, 'Migración', 1);

INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (19,'Convenio de Cooperación Interinstitucional con la Municipalidad Distrital de Santa Rosa', '¡Grandes noticias para el distrito de Santa Rosa! Hoy, el Presidente Ejecutivo del Instituto Catastral de Lima, Luis Molina Arles y el alcalde de la Municipalidad Distrital de Santa Rosa, George Robles, firmaron un Convenio Marco de Cooperación Técnico Interinstitucional. En la ceremonia, el Dr. Molina expresó el saludo del alcalde de la Municipalidad de Lima, Rafael López Aliaga ¿Qué significa esto? ¡La formulación y ejecución de proyectos que promuevan el desarrollo del catastro. ¡Sigamos trabajando en beneficio del desarrollo del distrito de Santa Rosa y el progreso de sus vecinos!', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/08/341529321_1606521256493806_6982873249408148708_n-1.jpg', 1, 'Migración', 1);

INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (20,'Convenio Marco de Cooperación Interinstitucional con la Municipalidad de Barranco', 'El presidente ejecutivo del Instituto Catastral de Lima, Dr. Luis Molina Arles, y la alcaldesa de Barranco, Jessica Vargas, suscribieron el Convenio Marco de Cooperación Inter-Institucional entre el ICL y la Municipalidad Distrital de Barranco. En la ceremonia, el Dr. Molina expresó el saludo del alcalde de Lima, Ing. Rafael López Aliaga, y resaltó la alianza entre ambas instituciones para aunar esfuerzos con el objetivo principal de que Barranco cuente con un catastro moderno y sobre todo actualizado, que es una herramienta esencial para el desarrollo del distrito y lograr el bienestar de los vecinos.', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/04/MDB_5676-1024x683.jpg', 1, 'Migración', 1);

INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (21,'Reunión de coordinación con el Servicio de Administración de Lima', 'El gerente de Cartografía y Tecnología de la Información del Instituto Catastal de Lima, Ing. Alonso Guardia junto a su equipo, sostuvieron una reunión productiva con la gerente de Fiscalización del SAT de Lima, Giovanna Retis, el gerente Central de Inovación y Proyectos, Waldir Tume y el gerente de Informática, Sandro Rodriguez, con el objetivo de trabajar de manera articulada en la actualización de los datos a nivel catastral de los predios de Cercado de Lima.', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/08/1A-1024x683.jpg', 1, 'Migración', 1);

INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (22,'El Instituto Catastral de Lima y la Municipalidad de la Molina', 'El gerente general del Instituto Catastral de Lima, Arq. Julio Fuentes, junto a otros funcionarios, sostuvieron una importante reunión con los representantes de la Municipalidad de la Molina, el gerente de Desarrollo Urbano, Eusebio Cabrera y el subgerente de Planeamiento, Inversiones y Modernización del Estado, Tatiana Naldos; donde ambas instituciones se mostraron interesados en trabajar de forma conjunta en el desarrollo de nuevas aplicaciones tecnológicas para acceder a la información cartográfica.', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/08/1C-1024x683.jpg', 1, 'Migración', 1);

INSERT INTO noticias (id,titulo_noticia, descripcion_noticia, fecha_noticia, id_categoria_noticia, url_imagen_portada, autorizado, autorizado_por, activo)
VALUES (23,'Convenio de Cooperación Interinstitucional con la Municipalidad Provincial de Ica', 'El alcalde de la Municipalidad Metropolitana de Lima; Dr. Rafael López Aliaga, extendió sus saludos a la ciudad del eterno sol a través del presidente ejecutivo del Instituto Catastral de Lima; Dr. Luis Alfonso Molina Arles, quien firmó junto al alcalde Provincial de Ica; Ing. Carlos Reyes Roque el Convenio Marco de Cooperación Inter-Institucional entre ambas instituciones. Gracias a esta alianza estratégica nos permitirá trabajar de manera articulada para la implementación de nuevas herramientas tecnológicas actualizadas a nivel catastral que mejorará el ordenamiento territorial y desarrollará estrategias para una mejor calidad de vida del pueblo iqueño.', 'Año 2023', 1, 'https://icl.gob.pe/wp-content/uploads/2023/08/1D-1024x683.jpg', 1, 'Migración', 1);

